&lt;h1 id=&quot;timer&quot;&gt;&lt;time&gt;00:00:00&lt;/time&gt;&lt;/h1&gt;
&lt;button id=&quot;start&quot;&gt;start&lt;/button&gt;
&lt;button id=&quot;stop&quot;&gt;stop&lt;/button&gt;
&lt;button id=&quot;clear&quot;&gt;clear&lt;/button&gt;
&lt;script&gt;
  var t1 = document.getElementById(&quot;timer&quot;),
    start = document.getElementById('start'),
    stop = document.getElementById('stop'),
    clear = document.getElementById('clear'),
    seconds = 0, minutes = 0, hours = 0,
    t;

function add() {
    seconds++;
    if (seconds &gt;= 60) {
        seconds = 0;
        minutes++;
        if (minutes &gt;= 60) {
            minutes = 0;
            hours++;
        }
    }
    
    t1.textContent = (hours ? (hours &gt; 9 ? hours : &quot;0&quot; + hours) : &quot;00&quot;) + &quot;:&quot; + (minutes ? (minutes &gt; 9 ? minutes : &quot;0&quot; + minutes) : &quot;00&quot;) + &quot;:&quot; + (seconds &gt; 9 ? seconds : &quot;0&quot; + seconds);

    timer();
}
function timer() {
    t = setTimeout(add, 1000);
}
timer();


/* Start button */
start.onclick = timer;

/* Stop button */
stop.onclick = function() {
    clearTimeout(t);
}

/* Clear button */
clear.onclick = function() {
    t1.textContent = &quot;00:00:00&quot;;
    seconds = 0; minutes = 0; hours = 0;
}
&lt;/script&gt;
